// preImmersive-[testing 03]-writingCodeWell-005-writing "assertObjectsEqual"

// input : object generated by function, object it should match, string describing the test
// output : log passed or failed message to console
// assumptions : "expected" objects will list properties in same order as "actual" objects

// stringify each object
// compare them
// log appropriate message to console
function assertObjectsEqual( actual, expected, testName ) {
  let actualStr = JSON.stringify( actual );
  let expectedStr = JSON.stringify( expected );
  if ( actualStr === expectedStr ) {
      console.log('passed')
  } else {
      console.log(`FAILED [${testName}] Expected ${expectedStr}, but got ${actualStr}`);
  }
}
var expected = {foo: 6, bar: 6};
var actual = {foo: 5, bar: 6}
assertObjectsEqual(actual, expected, 'test description');
